import React, { useState } from "react";

function App() {
  const [query, setQuery] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      const res = await fetch(`http://127.0.0.1:8000/search?query=${encodeURIComponent(query)}`);
      if (!res.ok) throw new Error("Backend error");
      const data = await res.json();
      setResult(data);
    } catch (err) {
      setError("Failed to fetch result");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white p-4 shadow">
        <h2 className="font-bold text-lg mb-4">CacheCraft Dashboard</h2>
        <div className="text-sm text-gray-600">(Dashboard stats here)</div>
      </aside>
      {/* Main */}
      <main className="flex-1 p-8">
        <form onSubmit={handleSearch} className="mb-6">
          <input
            className="border p-2 rounded w-80"
            placeholder="Search..."
            value={query}
            onChange={e => setQuery(e.target.value)}
          />
          <button className="ml-2 px-4 py-2 bg-blue-600 text-white rounded" type="submit" disabled={loading}>Search</button>
        </form>
        {loading && <div className="mb-4 text-blue-600">Loading...</div>}
        {error && <div className="mb-4 text-red-600">{error}</div>}
        {result && (
          <div className="bg-white p-4 rounded shadow">
            <div className="font-bold">{result.result}</div>
            <div className="text-xs mt-2">Source: {result.source}</div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
